mapper.update:
update life_order
pid = #{pid}, course_type = #{courseType}, status = #{status}, user_id = #{userId}, share_id = #{shareId}, course_id = #{courseId}, course_detail_id = #{courseDetailId}, remark = #{remark}, check_id = #{checkId}, coupon_id = #{couponId}, sale_user = #{saleUser}, total = #{total}, discounts = #{discounts}, pay = #{pay}, linkman = #{linkman}, phone = #{phone}, donate = #{donate}, course_duration = #{courseDuration}, consume_time = #{consumeTime}, order_time = #{orderTime}, use_time = #{useTime}, valid_refund_time = #{validRefundTime}, delete_flag = #{deleteFlag},
where order_id = #{orderId}

update life_order set donate = 0
where share_id = #{shareId}
AND (
  sale_user = -1 and user_id = #{userId} or sale_user = 0 and user_id != #{userId}
)
AND
consume_time >= #{start}
AND
status = 401
AND
donate = 1

mapper.select:
select sum(course_duration) from life_order
where share_id = #{shareId}
AND (
  sale_user = -1 and user_id = #{userId} or sale_user = 0 and user_id != #{userId}
)
AND
consume_time >= #{start}
AND
status = 401
AND
donate = 1
GROUP BY donate

select count(0) from life_order
where status != 301 and order_id in 

    #{item}

mapper.update:
update life_order set status = 102
where share_id = #{shareId} and status = 101 and order_id in

    #{item}

mapper.select:
select coupon_id from life_order where order_id in
#{item}
and status in (102,103,302) group by coupon_id

select count(0) from life_order
where coupon_id = #{couponId}
and status not in (102,103,302)

select count(0) as reserveNum,course_detail_id as courseDetailId,use_time as reserveNum from life_order where status in (102,103,302) and order_id in
#{item}
group by course_detail_id,use_time

