mapper.update:
update life_order set status = 301
where valid_refund_time >=now() and (share_id = #{shareId} or share_id is null and user_id = #{shareId}) and status = 201 and order_id in

    #{item}

update life_order set status = 201
where  (share_id = #{shareId} or share_id is null and user_id = #{shareId}) and status = 301 and order_id in

    #{item}

mapper.select:
select `order`.order_id as orderId, `order`.verification_code as verificationCode, `order`.status as status, course.img_url as imgUrl, course.`name` as courseName, classify.img as classifyImg, classify.course_classify_name as classifyName, `order`.pay as pay, `order`.total as total, child.`name` as saleName, `order`.sale_user as saleUser from life_order as `order` inner join life_course as course on course.course_id = `order`.course_id inner join life_course_classify as classify on classify.course_classify_id = course.course_classify_id left join life_user_child as child on child.child_id = `order`.sale_user where `order`.share_id = #{shareId}
and `order`.status = #{status}
and order_time > date_SUB(now(), INTERVAL 3 DAY)
order by `order`.order_time desc


where share_id = #{shareId}
and (
  sale_user = -1 and user_id = #{userId} or sale_user = 0 and user_id != #{userId}
)
and
consume_time >= #{start}
AND
status = 401
